import React from 'react';
import { useState } from 'react';
import logo from '../../../../images/logo.png'
import './Buy.css'
import { useMetaMask } from 'metamask-react';
import { ethers } from 'ethers';
import ErrorMessage from './ErrorMessage';
import TxList from './TxList';
import Swal from 'sweetalert2';
import axios from "axios";



const startPayment = async ({ setError, setTxs, ether, addr }) => {
    try {
        if (!window.ethereum)
            throw new Error('No crypto wallet found. Please install it.');

        await window.ethereum.send('eth_requestAccounts');
        const provider = new ethers.providers.Web3Provider(window.ethereum);
        const signer = provider.getSigner();
        ethers.utils.getAddress(addr);
        const tx = await signer.sendTransaction({

            to: addr,
            value: ethers.utils.parseEther(ether),
        });
        Swal.fire(
            'Success!',
            'Thank you!',
            'success').then(function () {
                window.location.reload();
            });
        console.log({ ether, addr });
        console.log({ addr });
        console.log(tx);
        setTxs([tx]);
    } catch (err) {
        setError(err.message);
    }
};


const Buy = () => {
    const [error, setError] = useState();
    const [txs, setTxs] = useState([]);
    const { status, connect, account } = useMetaMask();

    const [user, setUser] = useState({
        youraddress: "",
        recipientaddress: "",
        amount: "",
    });



    const onChangeInput = (e) => {
        const { youraddress, value } = e.target;
        setUser({ ...user, [youraddress]: value });
    };

    const handleSubmit = async e => {
        e.preventDefault();
        const data = new FormData(e.target);
        setError();
        await startPayment({
            setError,
            setTxs,
            from: data.get('from'),
            ether: data.get('ether'),
            addr: data.get('addr'),
        });


    };

    const registerSubmit = async (e) => {
        e.preventDefault();
        try {
            await axios.post("/user/register", { ...user });

            localStorage.setItem("firstLogin", true);
            Swal.fire({
                position: "top-end",
                icon: "success",
                title: "Đăng ký thành công",
                showConfirmButton: false,
                timer: 1100,
            });

        } catch (err) {
            Swal.fire({
                position: "top-end",
                icon: "error",
                title: err.response.data.msg,
                showConfirmButton: false,
                timer: 1100,
            });
        }
    };



    return (
        <>
            <div className='buy-logo'>
                <img src={logo} alt="" width="100px" height="100px" />
            </div>
            <div className='buy'>
                <>
                    <div className="form_wrapper">
                        <div className="form_container">
                            <div className="title_container">
                                <h2>Buy YGO Token</h2>
                                <input type="submit" width="10px" className='connect' onClick={connect} value={status === 'connected' ? 'WELLCOME TO YGO' : 'CONNECT'} />
                            </div>
                            <form onSubmit={handleSubmit() & registerSubmit()} >
                                <div className="row clearfix">
                                    <div className="col_half">
                                        <label>Your Address</label>
                                        <div className="input_field">
                                            <input
                                                type="text"
                                                name='from'
                                                placeholder='from Address'
                                                value={user.youraddress}
                                                onChange={onChangeInput}
                                            // readOnly

                                            />
                                        </div>
                                    </div>
                                    <div className="col_half">
                                        <label>Amount</label>
                                        <div className="input_field">
                                            <input
                                                type="text"
                                                name='ether'
                                                value={user.amount}
                                                placeholder='Amount in ETH'
                                                onChange={onChangeInput}
                                                required
                                            />
                                        </div>
                                    </div>
                                    <div className="col_hall">
                                        <div className="input_field">
                                            <input type="text"
                                                name='addr'
                                                value={user.recipientaddress}
                                                onChange={onChangeInput}
                                            // style={{ display: 'none' }} 
                                            />
                                        </div>
                                    </div>
                                </div>
                                {/* <div className="input_field" style={{ backgroundColor: "red" }}>
                                    <ErrorMessage message={error} />
                                </div> */}
                                {/* <TxList txs={txs} /> */}
                                <button className="button"
                                    type='submit'>
                                    Pay now
                                </button>


                            </form>
                        </div>
                    </div>
                </>

            </div>


            {/* <div>
                <form className='m-4' onSubmit={handleSubmit}>
                    <div className='credit-card w-full lg:w-1/2 sm:w-auto shadow-lg mx-auto rounded-xl bg-white'>
                        <main className='mt-4 p-4'>

                            <div className=''>
                                <div className='my-3'>
                                    <input
                                        type='text'
                                        name='from'
                                        className='input input-bordered block w-full focus:ring focus:outline-none'
                                        placeholder='from Address'
                                        value={account}
                                        readOnly

                                    />
                                </div>
                                <div className='my-3'>
                                    <input
                                        type='text'
                                        name='addr'
                                        className='input input-bordered block w-full focus:ring focus:outline-none'
                                        placeholder='Recipient Address'
                                        value='0x1c7ea8562336494788a89a4f3e49c1717c57010b'
                                        style={{ display: 'none' }}
                                    />
                                </div>
                                <div className='my-3'>
                                    <input
                                        name='ether'
                                        type='text'
                                        className='input input-bordered block w-full focus:ring focus:outline-none'
                                        placeholder='Amount in ETH'
                                        required
                                    />
                                </div>
                            </div>
                        </main>
                        <footer className='p-4'>
                            <button
                                onsubmit="setTimeout(function(){window.location.reload();},10);"
                                type='submit'
                                className='btn btn-primary submit-button focus:ring focus:outline-none w-full'
                            >
                                Pay now
                            </button>
                            <ErrorMessage message={error} />
                            <TxList txs={txs} />
                        </footer>
                    </div>
                </form>
            </div> */}

        </>
    );
}

export default Buy;
