import React, { Component } from 'react';


class SlideHome extends Component {

    constructor(props) {
        super(props);
        this.state = { name: "" }

    }
    changeTitle = (e) => {
        this.setState({ name: e.target.value * 100000 });
    }

    render() {
        return (
            <div>
                <p style={{ color: "black" }}>{this.state.name} </p>

            </div>
        );
    }
}

export default SlideHome;
